@PersistenceController
public class DesempenhoBimestralDAO extends JPACrud {

	private static final long serialVersionUID = 1L;

	@Override
	public List findByExample(DesempenhoBimestral example) {
		if (example == null) {
			example = new DesempenhoBimestral();
		}
		return super.findByExample(example);
	}

	public List findByCriteria(java.util.Map parameters) {
		if (parameters == null) {
			parameters = new java.util.HashMap();
		}
		CriteriaBuilder cb = super.getCriteriaBuilder();
		CriteriaQuery q = cb.createQuery(DesempenhoBimestral.class);
		Root r = q.from(DesempenhoBimestral.class);
		Predicate where = null;
		Object parameter = null; 
		parameter = parameters.get("idBimestre");
		if (parameter != null) {
			Predicate expression = null;
			if (parameter instanceof java.util.List) {
				@SuppressWarnings("unchecked")
				List collection = (List)parameter;
				expression = r.get("nota1").in(collection);
			} else if (parameter instanceof Double) {
				expression = cb.equal(r.get("nota1"), parameter);
			}
			if (expression != null) {
				if (where == null) {
					where = expression;
				} else {
					where = cb.and(where, expression);
